import React, { useState, useEffect, useRef } from 'react';
import './DigitalRain.css';

const chars = ['01', 'あ', 'ア', 'ア', 'イ', 'イ', 'ウ', 'ウ', 'ABCDEFGHIJKLMNOPQRSTUVWXYZ'];
const getRandomChar = () => chars[Math.floor(Math.random() * chars.length)].charAt(Math.floor(Math.random() * 2));

export default function DigitalRain() {
  const containerRef = useRef(null);
  const [drops, setDrops] = useState([]);

  useEffect(() => {
    const updateSize = () => {
      const colCount = Math.floor(window.innerWidth / 20);
      setDrops(Array(colCount).fill(0));
    };

    updateSize();
    window.addEventListener('resize', updateSize);
    return () => window.removeEventListener('resize', updateSize);
  }, []);

  useEffect(() => {
    let animationFrame;

    const animate = () => {
      setDrops(prev => {
        return prev.map((start) => {
          if (start > 100 || Math.random() > 0.975) 
            return 0;
          return start + 1;
        });
      });
      animationFrame = requestAnimationFrame(animate);
    };

    animate();
    return () => cancelAnimationFrame(animationFrame);
  }, []);

  return (
    <div ref={containerRef} className="digital-rain">
      {drops.map((start, i) => (
        <div 
          key={i}
          className="drop"
          style={{
            left: `${i * 20}px`,
            animationDelay: `${Math.random() * 2}s`,
            animationDuration: `${0.5 + Math.random() * 2}s`
          }}
        >
          {Array(Math.floor(window.innerHeight / 24)).fill(0).map((_, j) => (
            <span
              key={`${i}-${j}`}
              style={{
                opacity: (j === 0) ? 1 : 0.7/(j*0.3),
                filter: `brightness(${3 - j*0.5})`,
                transform: `translateY(${start}px)`
              }}
            >
              {getRandomChar()}
            </span>
          ))}
        </div>
      ))}
    </div>
  );
}
